mapper.update:
update review
set readcount = readcount + 1
where review_num = #{review_num}

mapper.select:
SELECT X.rn, X.review_num, X.title, X.content, X.review_date, X.K_NAME, X.L_NUM, X.mem_num, X.mem_nickname, X.ratingAvg FROM ( SELECT ROWNUM AS RN, A.review_num, A.title, A.content, A.review_date, A.K_NAME, A.L_NUM, A.mem_num, A.mem_nickname, A.ratingAvg FROM ( SELECT r.review_num, r.title, r.content, r.review_date, l.K_NAME, l.L_NUM, m.mem_num, m.mem_nickname, (SELECT AVG(rt.rating) FROM rating rt WHERE rt.review_num = r.review_num) AS ratingAvg FROM review r JOIN local l ON r.L_NUM = l.L_NUM JOIN member m ON r.mem_num = m.mem_num AND m.mem_num = #{mem_num}
AND l.L_NUM = #{area}
AND title LIKE '%' || #{keyword} || '%'
AND content LIKE '%' || #{keyword} || '%'
ORDER BY review_num DESC
ORDER BY ratingAvg DESC NULLS LAST , review_num DESC
) A WHERE ROWNUM <= ( ${(pageNum -1) * pageRowCount} + ${pageRowCount} ) ) X WHERE X.rn >= ( ${(pageNum -1) * pageRowCount} +1 )
